import React, { useState } from "react";

export default function Textarea(props) {
  const [text, setText] = useState("Enter text here");
  return (
    <>
      <h1>{props.heading}</h1>
      <div className="mb-3">
        <textarea
          class="form-control"
          value={text}
          id="mybox"
          rows="8"
        ></textarea>
      </div>
      <button className="">Convert to Uppercase</button>
    </>
  );
}
