import React, { useState } from "react";
import Navbar from "./Components/navbar";
import Textarea from "./Components/textform";
// import About from "./Components/about";

function App() {
  const [mode, setmode] = useState("light");
  const [btnText, setBtnText] = useState("Enable Dark Mode");

  const toogleStyle = () => {
    if (mode === "light") {
      setmode("dark");
      setBtnText("Enable Light Mode");
    } else {
      setmode("light");
      setBtnText("Enable Dark Mode");
    }
  };
  <button onClick={toogleStyle}>{btnText} </button>;
  return (
    <div>
      <Navbar mode={mode} toogleStyle={toogleStyle} />

      <div className="container my-3 " style={mode}>
        <Textarea heading="Enter the text to analyze" style={mode} />
      </div>
      {/* <About /> */}
    </div>
  );
}

export default App;
